import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import Post from "./Post"
import { db } from '../firebase'
import firebase from '../firebase'
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { storage } from '../firebase'

class Index extends React.Component{
    constructor(props){
        super(props)
        this.state={
            posts:[],
            postpage:false,
            user:null,
            pictureurl:""


        }
    }

    changepost=()=>{
        this.setState({
        postpage:false
    })
    }
    componentDidMount(){


        firebase.auth().onAuthStateChanged(user => {
            this.setState({ user })
          })


        const postsref = db.collection("posts").orderBy('timestamp', 'desc')
        postsref.limit(5).onSnapshot((snapshot) => {
            const posts = snapshot.docs.map( (postdoc) =>{

                 const post = postdoc.data();
                //  console.log(post);

                 const pathref = storage.ref().child(`images/${post.postpicname}`)
                 pathref.getDownloadURL().then((url)=>{
                     this.setState({
                        pictureurl:url
                     })
                 }
                 )
                 if(post.course=="web"){
                return {
                    title:post.title,
                    body:post.body,
                    name:post.name,
                    pic:post.pic,
                    course:post.course,
                    nickname: post.nickname,
                    favcount: post.favcount,
                    librarycount: post.librarycount,
                    timestamp: post.timestamp,
                    postpicname: post.postpicname
                }   
            }

            })
            this.setState({
                posts : posts,

            })

        });
    }


    post = () =>{
            this.setState({
        postpage:false
    })
            this.setState({
                postpage : true
            })  
    }




    render(){

        return(
            <div>



                    {this.props.user?(
                     this.state.posts.map((post, i) => { 
                        return ( 

                            <div key={i}>
                               <div className="post">
                                    <div className="">{post.name}</div>
                                    <div className="">{post.nickname}</div>
                                    <div className="">{post.pictureurl}</div>
                                    <div className="">{post.title}</div>
                                    <div className="">{post.body}</div>
                                    <div className="">{post.course}</div>
                                    <div className="">{post.favcount}</div>
                                    <div className="">{post.librarycount}</div>
                                    <div className="">{post.librarycount}</div>
                                </div>
                            </div>


                        )

                    })
                )   
                :
                (
                    <div></div>
                )
                }
            {this.props.user?(
                this.state.postpage?(
                    <div>
                    <div><Post user = {this.state.user} postpage={this.state.postpage} changepost={this.changepost}/></div>
                    <button onClick={this.changepost}>投稿をやめる</button>
                    </div>
                )
                :
                (
                    <a className="btn-floating btn-large waves-effect waves-light red" onClick={this.post}><i class="material-icons">add</i></a>

                )
            )   
            :
            (
                <div></div>
            )
            }

            </div>
        );
    }

}

export default Index
