import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import { BrowserRouter, Route, Link } from 'react-router-dom'
import About from "./glineAbout";
import Help from "./help";
import Post from "./post";
import firebase from '../firebase'




class Login extends React.Component{
    constructor(){
        super()
        this.state = {
            user:null
        }

    }


    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
          this.setState({ user })
        })
      }

    login() {
        const provider = new firebase.auth.GoogleAuthProvider()
        firebase.auth().signInWithRedirect(provider)
        firebase.auth().signInWithPopup(provider).then(function(result) {
            // This gives you a Google Access Token. You can use it to access the Google API.
            var token = result.credential.accessToken;
            // The signed-in user info.
            var user = result.user;
            // ...
          }).catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // The email of the user's account used.
            var email = error.email;
            // The firebase.auth.AuthCredential type that was used.
            var credential = error.credential;
            // ...
          });
          firebase.auth().getRedirectResult().then(function(result) {
            if (result.credential) {
              // This gives you a Google Access Token. You can use it to access the Google API.
              var token = result.credential.accessToken;
              // ...
            }
            // The signed-in user info.
            var user = result.user;
          }).catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // The email of the user's account used.
            var email = error.email;
            // The firebase.auth.AuthCredential type that was used.
            var credential = error.credential;
            // ...
          });
      }

    logout() {
        firebase.auth().signOut().then(this.setState({
            user:null
        })
        )
      }

    render(){

        return(
            <BrowserRouter>
            <div>
                 <header>

                    <div className="header-content">
                        <ul className="header-list">
                            <li className="header-logo"><Link to='/login'>GLINE</Link></li>
                            <li className ="header-overview"><Link to='/about'>GLINEの概要</Link></li>
                            <li className="header-help"><Link to='/help'>ヘルプ</Link></li>
                            <li className="header-login" onClick={this.login}><Link to='/login'>ログイン</Link></li>

                        </ul>

                        <Route path='/post' component={Post} />
                        <Route path='/about' component={About} />
                        <Route path='/help' component={Help} />
                    </div>


                </header>
                <div className="main">
                <div className="App">
                     <p className="App-intro">
          UID: {this.state.user && this.state.user.uid}
        </p>


      </div>
      {this.state.user ? (

                <li className="gline-access" onClick={this.logout}><Link to='/login'>ログアウト</Link></li>
                ) : (
                    <li className="gline-access" onClick={this.login}><Link to='/login'>Glineにアクセス</Link></li>
                    )}
                </div>

                <footer>
                    <div className="footer-content">

                    </div>
                </footer>
            </div>
            </BrowserRouter>
        );
    }
}



export default Login
