//解答の閲覧画面のコンポーネントを編集
import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import { BrowserRouter, Route, Link } from 'react-router-dom';
import firebase, { db, storage } from '../firebase';

class Response extends React.Component{
    constructor(props){
        super(props)
        this.state={

            text : "",
            responseimageurls:[],
            responses:[]

         }
         this.handleInputText = this.handleInputText.bind(this);

         this.handleFileSelect = this.handleFileSelect.bind(this);
     }


     save = (e) => {



        if(this.state.files!=[]){
             e.preventDefault()
             this.state.files.forEach((file)=>{

                 storage.ref().child(`images/${file.name}`).put(file).then(snap => {
                     console.log('Uploaded a blob or file!');
                 });
             })
         }   
         let id =0;
         let post_id = String(id)
         const user = firebase.auth().currentUser
         const userdb = db.collection("users").doc(user.uid);
         userdb.get().then((userdb) => {
             if (userdb.exists) {
                 console.log("Document data:", userdb.data());

                     if(this.state.files==null){
                         db.collection("Responses").doc().set({
                             post_id : this.props.match.params.id,
                             name: userdb.data().name,
                             pic: userdb.data().pic,
                             email: userdb.data().email,
                             course: userdb.data().course,
                             nickname: userdb.data().nickname,
                             text: this.state.text,
                             title: this.state.title,
                             favcount: 0,                           
                             timestamp: new Date(),                            
                             responseimageurl:[]

                         })        
                         .then(() => {


                             console.log(`追加に成功しました `);
                             this.props.changepost()
                         })
                         .catch((error) => {
                             console.log(`追加に失敗しました (${error})`);
                         });
                     }else{
                         db.collection("Responses").doc().set({
                            post_id : this.props.match.params.id,
                             name: userdb.data().name,
                             pic: userdb.data().pic,
                             email: userdb.data().email,
                             course: userdb.data().course,
                             nickname: userdb.data().nickname,
                             text: this.state.text,
                             title: this.state.title,
                             favcount: 0,                           
                             timestamp: new Date(),                            



                         })        
                         .then(() => {

                             console.log(`追加に成功しました `);

                         })
                         .catch((error) => {
                             console.log(`追加に失敗しました (${error})`);
                         });



                     }
             } else {
                 // doc.data() will be undefined in this case
                 console.log("No such document!");
             }

         })
         .catch(function(error){
         console.log(`取得失敗 (${error})`);
             });
     }


    handleInputText(event) {
        this.setState({

            text: event.target.value
        })
    }


    handleFileSelect = (e) => {
        const files = e.target.files
        Array.prototype.forEach.call(files,(file) => {
            const reader = new FileReader()
            reader.addEventListener("load",()=>{
                this.setState((state)=>{
                    state.postimageurls.push(reader.result)
                    state.files.push(file)
                    return state
                })

             })
            if(file){
                reader.readAsDataURL(file)
            }
        });

    }

    render(){

        return(
            <div>
             <div className="posttitle">回答を入力:
                            <input type='text' value={this.state.Text} onChange={this.handleInputText.bind(this)} />
                        </div>

                        <div className="file-field input-field">
                            <div className="btn">
                                <span>File</span>
                                <input type="file" onChange={this.handleFileSelect.bind(this)} multiple />
                            </div>
                            <div className="file-path-wrapper">
                                <input className="file-path validate" type="text"/>
                            </div>
                            {
                                this.state.postimageurls.map((imageurl,i)=>{
                                    return (
                                    <div key={i}>
                                        <img src={imageurl}></img>
                                    </div>
                                    )
                                })
                            }

                        </div>
                        <button onClick={this.save}  ><Link to='/posts'>回答を投稿する</Link></button>
                        </div>


        )
    }
}

export default Response
