import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import { BrowserRouter, Route, Link } from 'react-router-dom'
import About from "./glineAbout";
import Help from "./help";
import Post from "./post";



class Login extends React.Component{
    constructor(){
        super()
        this.state={

        }

    }

    function(){
        // #で始まるアンカーをクリックした場合に処理
        $('a[href^=#]').click(function() {
        // スクロールの速度
        var speed = 400; // ミリ秒
        // アンカーの値取得
        var href= $(this).attr("href");
        // 移動先を取得
        var target = $(href == "#" || href == "" ? 'html' : href);
        // 移動先を数値で取得
        var position = target.offset().top;
        // スムーススクロール
        $('body,html').animate({scrollTop:position}, speed, 'swing');
        return false;
        });
      };

    render(){

        return(
            <BrowserRouter>
            <div>
                 <header>

                    <div className="header-content">
                        <ul className="header-list">
                            <li className="header-logo"><Link to='/login'>GLINE</Link></li>
                            <li className ="header-overview"><Link to='/about'>GLINEの概要</Link></li>
                            <li className="header-help"><Link to='/help'>ヘルプ</Link></li>
                            <li className="header-login"><Link to='/post'>ログイン</Link></li>
                        </ul>

                        <Route path='/post' component={Post} />
                        <Route path='/about' component={About} />
                        <Route path='/help' component={Help} />
                    </div>


                </header>
                <div className="main">
                <li className="gline-access"><Link to='/post'>GLINEにアクセス</Link></li>


                </div>

                <footer>
                    <div className="footer-content">

                    </div>
                </footer>
            </div>
            </BrowserRouter>
        );
    }
}



export default Login
