import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import { BrowserRouter, Route, Link } from 'react-router-dom'
import Post from "./post";
import firebase from '../firebase'




class Index extends React.Component{
    constructor(){
        super()
        this.state = {
            user:null
        }

    }

    logout() {
        firebase.auth().signOut()
      }

    render(){

        return(
            <BrowserRouter>
            <div>
                 <header>
                 {this.state.user ? (
                    <div className="header-content">
                        <ul className="header-list">
                            <li className="header-logo"><Link to='/web'>WEB</Link></li>
                            <li className ="header-overview"><Link to='/game'>GAME</Link></li>
                            <li className="header-help"><Link to='/iphone'>iPhone</Link></li>
                             {/* <Route path='/web' component={Web} />
                        <Route path='/game' component={Game} />
                        <Route path='/iphone' component={Iphone} /> */}

                        </ul>
                        </div>
                        ):(
                            <div className="header-content"></div>




                          )}



                </header>
                <div className="main">
                <div className="App">
                     <p className="App-intro">
          UID: {this.state.user && this.state.user.uid}
        </p>


      </div>
      {this.state.user ? (

                <li className="gline-access" onClick={this.logout}><Link to='/login'>ログアウト</Link></li>
                ) : (
                    <li className="gline-access" onClick={this.login}><Link to='/login'>Glineにアクセス</Link></li>
                    )}
                </div>

                <footer>
                    <div className="footer-content">

                    </div>
                </footer>
            </div>
            </BrowserRouter>
        );
    }
}



export default Index
