import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { db } from '../firebase'
import firebase from '../firebase'





class Login extends React.Component{
    constructor(props){
        super(props)
        this.state={
            course: '',
            dispText: ''
        }
        this.handleInput = this.handleInput.bind(this);
    this.send = this.save.bind(this);
    }

    handleInputText(event) {
        this.setState({
          dispText: event.target.value
        })
      }　　

    handleInput({ target: { value } }) {
        this.setState({
          course: value
        });
      }

    save=(e)=>{
        e.preventDefault()

        const user = firebase.auth().currentUser
        if( user ){
            db.collection("users").doc(user.uid).set({
                name: user.displayName,
                pic: user.photoURL || '/images/profile_placeholder.png',
                email: user.email,
                course: this.state.course
            })
            .then(() => {
                console.log(`追加に成功しました `);
            })
            .catch((error) => {
                console.log(`追加に失敗しました (${error})`);
            });
        }
    }


    render(){
        console.log(this.props.user)
        return(
            <BrowserRouter>
            <div>
                 <header>
                 { this.props.user?
                 (
                    <div className="header-content">
                        <ul className="header-list">

                        </ul>
                        <div>
                        <div>InputText:
                        <input type='text' value={this.state.dispText} onChange={this.handleInputText.bind(this)}/>
                        <h2>{this.state.dispText}</h2>
                        </div>
                        <form>
                        <select name = "course" value={this.state.course} onChange={this.handleInput}>
                            <option value="web"> web</option>
                            <option value="game"> game</option>
                            <option value="iphone">iphone</option>
                        </select>

                        <button onClick={this.save}><Link to='/index'>geek-lineに登録</Link></button>

                        </form>
                        </div>
                    </div>
                )
                :
                (
                    <div className="header-content">


                    </div>




                )}  
                </header>
                <div className="main">
                <div className="App">
                     <p className="App-intro">

        </p>


      </div>
      {this.props.user ? (
          <div>
                <div>名前: {this.props.user && this.props.user.displayName}</div>
                <div>E-mail: {this.props.user && this.props.user.email}</div>
                <div><img src  ={this.props.user.photoURL || '/images/profile_placeholder.png'}></img></div>

                <li className="gline-logout" onClick={this.logout}><Link to='/login'>ログアウト</Link></li>
                </div>
                ) : (
                   <div></div>
                    )}
                </div>

                <footer>
                    <div className="footer-content">

                    </div>
                </footer>
            </div>
            </BrowserRouter>
        );
    }
}



export default Login
