import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import Post from "./post"
import { db } from '../firebase'
import firebase from '../firebase'
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { storage } from '../firebase'
import Login from "./login";

class Index extends React.Component{
    constructor(props){
        super(props)
        this.state={
            posts:[],
            postpage:false,
            pictureurl:"",
            usercourse:""

        }
    }

    changepost=()=>{
        if(this.state.postpage==true){
            this.setState({
                postpage:false
            })
        }else{
            this.setState({
                postpage:true
            })
        }

    }



    componentDidMount=()=>{
        const user = firebase.auth().currentUser


        console.log(this.props.user)
        if (user) {
        const userdb = db.collection("users").doc(this.state.user.uid);
        userdb.get().then((userdb) => {
            if (userdb.exists) {
                this.setState({
                    usercourse:userdb.data().course
                })
                console.log("Document data:", userdb.data());
            }

        }
        )
    }

        const postsref = db.collection("posts").orderBy('timestamp', 'desc')
        postsref.get().then((snapshot) => {
            const posts = snapshot.docs.map( (postdoc) =>{

                 const post = postdoc.data();
                 const pathref = storage.ref(`images/${post.postpicname}`)
                 pathref.getDownloadURL().then((url)=>{
                     this.setState({
                        pictureurl:url
                     })
                 }
                 )

                return {
                    title:post.title,
                    body:post.body,
                    name:post.name,
                    pic:post.pic,
                    course:post.course,
                    nickname: post.nickname,
                    favcount: post.favcount,
                    librarycount: post.librarycount,
                    timestamp: post.timestamp,
                    postpicname: post.postpicname
                }   

            })
            this.setState({
                posts : posts,

            })

        });



    }





    render(){
        console.log(this.props.user)
        return(
            <div>

                {

                    this.state.usercourse==""?(
                        <Login usercourse={this.state.usercourse}/>
                    )
                    :
                    (

                        this.state.posts.map((post, i) => { 
                                return ( 
                                    <div key={i}>
                                        {post.title}
                                        {post.body}
                                        {post.name}
                                        <img src={this.state.pictureurl}></img> 

                                        {post.course}
                                        {post.nickname}
                                        {post.favcount}
                                        {post.librarycount}
                                        {post.timestamp.toString()}
                                    </div>
                                )

                        })

                    )   
                }

                {
                    this.state.usercourse==""?(
                        <div></div>
                    )

                    :(
                    this.state.postpage?(
                        <div>
                        <div><Post user = {this.state.user} postpage={this.state.postpage} changepost={this.changepost}/></div>
                        <button onClick={this.changepost}>投稿をやめる</button>
                        </div>
                    )
                    :
                    (
                        <button onClick={this.changepost}>投稿する</button>
                    )
                    )
                }

            </div>
        );
    }

}

export default Index
