import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import Post from "./Post"
import { db } from '../firebase'
import firebase from '../firebase'
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { storage } from '../firebase'
import Posts from "./Posts";
import Response from "./Response"

class Detail extends React.Component{
    constructor(props){
        super(props)
        this.state={
           post:[],
           answered:false,
           answerdisplay:false
        }
    }


    componentWillMount(){



        const postsref = db.collection("posts").orderBy('timestamp', 'desc')
        postsref.limit(20).onSnapshot((snapshot) => {
            const posts = snapshot.docs.map( (postdoc) =>{

                const post = postdoc.data();
                    //  console.log(post);
                if(post.postimageurl!=[]){
                    const pathref = storage.ref().child(`images/${post.postimageurl}`)
                    pathref.getDownloadURL().then((url)=>{ 

                        this.setState((state)=>{
                            const index =  state.posts.findIndex((post)=>{
                                return post.id === postdoc.id;
                            })
                            state.posts[index].postimageurl.push(url)



                            return state
                        })
                    })


                }
                return {...post,id:postdoc.id}


            })
            console.log(posts)
            const uid =this.props.match.params.id
            const post= posts.find((posts) => {return (posts.id === uid);})
            this.setState({
                post:post
            })

        });
        const user = firebase.auth().currentUser
        if(user.email==this.state.post.email){
            this.setState({
            answerdisplay:true
            })
        }


    }
    answered(){
        db.collection("posts").doc().set({
            post_id : db.collection("posts").doc(),
            name: userdb.data().name,
            pic: userdb.data().pic,
            email: userdb.data().email,
            course: userdb.data().course,
            nickname: userdb.data().nickname,
            body: this.state.body,
            title: this.state.title,
            favcount: 0,
            librarycount: 0,
            timestamp: new Date(),
            answered:this.state.answered,
            postimageurl:[]

        })        
        .then(() => {

            console.log(`追加に成功しました `);
            this.props.changepost()
        })
        .catch((error) => {
            console.log(`追加に失敗しました (${error})`);
        });
    }


    render(){

        return(
            <div>

                {this.props.user&&


                           <div>
                                {this.state.post.title}
                                {this.state.post.body}
                                {this.state.post.name}
                                {this.state.post.pictureurl}

                                {this.state.post.course}
                                {this.state.post.nickname}
                                {this.state.post.favcount}
                                {this.state.post.librarycount}

                            </div> 



                }
                {this.state.answerdisplay&&
                    <div>
                        <button onClick={this.ansewered}>回答済みにする</button>
                    </div>

                }

            <Response match={this.props.match}/>
            </div>
        )
    }
}

export default Detail
