import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { db } from '../firebase'
import Post from "./post";
import firebase from '../firebase'




class Index extends React.Component{
    constructor(props){
        super(props)
        this.state={
            value: '',
            message: ''
        }
        this.handleInput = this.handleInput.bind(this);
    this.send = this.save.bind(this);
    }


    handleInput({ target: { value } }) {
        this.setState({
          value
        });
      }

    save=()=>{
        const { value } = this.state;
        this.setState({
            value: '',
            message: value
          });

    db.collection("users").add({
        name: this.props.user.displayName,
        pic:  this.props.user.photoURL || '/images/profile_placeholder.png',
        email: this.props.user.email,
        course:this.state.value
      })
      .then((doc) => {
        console.log(`追加に成功しました (${doc.id})`);
      })
      .catch((error) => {
        console.log(`追加に失敗しました (${error})`);
      });
    }


    render(){
        console.log(this.props.user)
        return(
            <BrowserRouter>
            <div>
                 <header>
                 { this.props.user?
                 (
                    <div className="header-content">
                        <ul className="header-list">
                            <li className="header-web"><Link to='/web'>WEB</Link></li>
                            <li className ="header-game"><Link to='/game'>GAME</Link></li>
                            <li className="header-iphone"><Link to='/iphone'>iPhone</Link></li>

                            <div class="input-field col s12">
                            <select multiple>
                            <option value="" disabled selected>Choose your option</option>
                            <option value="1">Option 1</option>
                            <option value="2">Option 2</option>
                            <option value="3">Option 3</option>
                            </select>
                            <label>Materialize Multiple Select</label>
                            </div>


                            <input type="text" value={this.state.value} onChange={this.handleInput} />

                            <button onClick={this.save} class="btn waves-effect waves-light" type="submit" name="action">Submit<i class="material-icons right">send</i></button>

                             {/* <Route path='/web' component={Web} />
                        <Route path='/game' component={Game} />
                        <Route path='/iphone' component={Iphone} /> */}

                        </ul>
                    </div>
                )
                :
                (
                    <div className="header-content">


                    </div>




                )}  
                </header>
                <div className="main">
                <div className="App">
                     <p className="App-intro">

        </p>


      </div>
      {this.props.user ? (
          <div>
                <div>名前: {this.props.user && this.props.user.displayName}</div>
                <div>E-mail: {this.props.user && this.props.user.email}</div>
                <div>picture: <img src  ={this.props.user.photoURL || '/images/profile_placeholder.png'}></img></div>
                <li className="gline-logout" onClick={this.logout}><Link to='/login'>ログアウト</Link></li>
                </div>
                ) : (
                   <div></div>
                    )}
                </div>

                <footer>
                    <div className="footer-content">

                    </div>
                </footer>
            </div>
            </BrowserRouter>
        );
    }
}



export default Index
