import React from 'react';
import Login from "./components/login";
import Index from "./components/index";
import firebase from './firebase'
import { db } from './firebase'
import About from "./components/glineAbout";
import { BrowserRouter, Route, Link } from 'react-router-dom'


class App extends React.Component {
  constructor(){
    super()
    this.state = {
        user:null,
        isLogging:false
    }

  }

  componentDidMount= () =>{
    firebase.auth().onAuthStateChanged(user => {
      this.setState({ user })
    })
    db.collection("users").add({
      name: this.user.displayName,
      pic: this.user.photoURL || '/images/profile_placeholder.png',
      email: this.user.email
    })
    .then((doc) => {
      console.log(`追加に成功しました (${doc.id})`);
    })
    .catch((error) => {
      console.log(`追加に失敗しました (${error})`);
    });

  }

  login=()=> {
    const provider = new firebase.auth.GoogleAuthProvider()
    firebase.auth().signInWithPopup(provider)
    this.setState({ isLogging:true }) 

  }

  logout=()=> {
    firebase.auth().signOut()
    this.setState({ isLogging:false }) 
  }



  render(){
    return (
      <div className="App">

        <Login login={this.login } logout={this.logout} user ={this.state.user} isLogging = {this.state.isLogging}/>
        <Index logout={this.logout} user ={this.state.user} isLogging = {this.state.isLogging}/>

        <BrowserRouter>
        <Route path='/about' component={About} />
        </BrowserRouter>


      </div>
    );
  }
}
export default App;
