import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";

import { BrowserRouter, Route, Link } from 'react-router-dom'
import { db } from '../firebase'
import firebase from '../firebase'
import Index from './Index'
import PropTypes from 'prop-types';

import "./style.css"
import LoginHeader from './items/Login-header'



class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            user:[],
            logined: true
        }

        this.save = this.save.bind(this);
    }

    componentDidMount() {
        console.log("login")



        console.log(this.state.user)
      }


    // coursecheck = () => {
    //     var userdb = db.collection('users').doc('user.uid');
    //     if (userdb.course == "") {

    //         return true
    //     } else {
    //         return false
    //     }
    // }





    save = (e) => {

        const user = firebase.auth().currentUser
        if (user) {
            db.collection("users").doc(user.uid).set({
                name: user.displayName,
                pic: user.photoURL || '/images/profile_placeholder.png',
                email: user.email,
                course: this.state.course,
                nickname: this.state.nickname
            })
                .then(() => {
                    this.setState({
                        logined : true
                    })
                    console.log(`追加に成功しました `);
                })
                .catch((error) => {
                    console.log(`追加に失敗しました (${error})`);
                });
        }


    }


    render() {
        console.log(this.props.user)
        return (


                <div>
                    <header>


                    </header>
                    <div className="main">
                        <div className="App">
                            <p className="App-intro">

                            </p>


                        </div>
                    {this.state.logined?(

                        this.props.user ? (
                            <div>
                                <div className="plfname">名前: {this.props.user && this.props.user.displayName}</div>
                                <div className="plfemail">E-mail: {this.props.user && this.props.user.email}</div>
                                <div className="plf">コース: {this.props.user && this.props.user.web}</div>
                               <form>
                                    <div className="plfnickname">ニックネームを入力:
                    <input type='text' value={this.props.nickname} onChange={this.props.handleInputnickname} />
                                    </div>
                                    <div className="plfcourse">コースを入力:
                    <input type='text' value={this.props.course} onChange={this.props.handleInputcourse}/>
                                    </div>


                                    {this.props.nickname == '' ?
                                        (
                                            <div>名前を入力してください</div>
                                        ) : (
                                            this.props.course == 'game' || this.props.course == 'web' || this.props.course == 'iphone' ?
                                                (
                                                    <button onClick={this.save}><Link to='/posts/index'>geek-lineに登録</Link></button>

                                                ) : (
                                                    <div>コースを入力してください(WEBコース="web",GAMEコース="game",iPhoneコース="iphone")</div>

                                                )
                                        )

                                    }


                                </form>

                                <li className="gline-logout" onClick={this.props.logout}><Link to='/'>ログアウト</Link></li>
                            </div>
                        ) : (
                                <div></div>
                            )




                    )
                    :
                    (
                        <div></div>  
                    )
                    }

</div>
                    <footer>
                        <div className="footer-content">

                        </div>
                    </footer>
                </div>



        );
    }
}
Login.propTypes = {
      user: PropTypes.string.isRequired,
    };




export default Login
