import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import Post from "./Post"
import { db } from '../firebase'
import firebase from '../firebase'
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { storage } from '../firebase'
import Detail from "./Detail"


class Posts extends React.Component{
    constructor(props){
        super(props)
        this.state={
            posts:[],
            postpage:false,
            user:null,
            pictureurl:"",
            detail:[]


        }

    }

    changepost=()=>{
        this.setState({
        postpage:false
    })
    }

    componentDidMount(){


        firebase.auth().onAuthStateChanged(user => {
            this.setState({ user })
          })

        const postsref = db.collection("posts").orderBy('timestamp', 'desc')
        postsref.limit(20).onSnapshot((snapshot) => {
            const posts = snapshot.docs.map( (postdoc) =>{

                const post = postdoc.data();
                    //  console.log(post);
                if(post.postimageurl!=[]){
                    const pathref = storage.ref().child(`images/${post.postimageurl}`)
                    pathref.getDownloadURL().then((url)=>{ 

                        this.setState((state)=>{
                            const index =  state.posts.findIndex((post)=>{
                                return post.id === postdoc.id;
                            })
                            state.posts[index].postimageurl.push(url)



                            return state
                        })
                    })


                }
                return {...post,id:postdoc.id}


            })
            this.setState({
                posts : posts,

            })

        });


    }


    post = () =>{
        if(this.state.postpage==true){
        this.setState({
            postpage:false
        })
         }else{
        this.setState({
            postpage : true
        })  
    }
    }


    render(){
        console.log(this.state)
        return(
            <div>

                {this.props.user&&
                <div>
                 <Route  path='/posts/index' render={()=><Index  posts={this.state.posts} user ={this.props.user} post = {this.post}/>}/>
                 <Route  path='/posts/:id' render={()=><Detail  logout={this.logout} user ={this.state.user} isLogging = {this.state.isLogging} index ={this.state.index}/>}/>
                </div>
                }



            </div>
        );
    }

}

export default Posts
