import React from 'react';
import Login from "./components/login";
import Index from "./components/index";
import firebase from './firebase'
import { db } from './firebase'


class App extends React.Component {
  constructor(){
    super()
    this.state = {
        user:null,
        isLogging:false
    }

  }

  componentDidMount() {
    firebase.auth().onAuthStateChanged(user => {
      this.setState({ user })
    })
  }

  login=()=> {
    const provider = new firebase.auth.GoogleAuthProvider()
    firebase.auth().signInWithPopup(provider)
    this.setState({ isLogging:true }) 
    db.collection("user").doc("user_id").set({
      name: this.displayName,
      pic: this.photoURL || '/images/profile_placeholder.png',
      email: this.email
    })
  }

  logout=()=> {
    firebase.auth().signOut()
    this.setState({ isLogging:false }) 
  }



  render(){
    return (
      <div className="App">

        <Login login={this.login } logout={this.logout} user ={this.state.user} isLogging = {this.state.isLogging}/>
        <Index logout={this.logout} user ={this.state.user} isLogging = {this.state.isLogging}/>


      </div>
    );
  }
}
export default App;
