import React from "react";
import "../bower_components/materialize/dist/css/materialize.css";
import "./style.css";
import Post from "./Post"
import { db } from '../firebase'
import firebase from '../firebase'
import { BrowserRouter, Route, Link } from 'react-router-dom'
import { storage } from '../firebase'
import Detail from "./Detail"


class Index extends React.Component{
    constructor(props){
        super(props)
        this.state={
            posts:[],
            postpage:false,
            user:null,
            pictureurl:"",
            detail:[]


        }

    }






    post = () =>{
        if(this.state.postpage==true){
        this.setState({
            postpage:false
        })
         }else{
        this.setState({
            postpage : true
        })  
    }
    }


    render(){
        console.log(this.state)
        return(
            <div>

                {this.props.user&&
                this.props.postpage==false&&
                     this.props.posts.map((post,i) => { 


                      return(
                            <div key={i}>
                                <Link to={`/posts/${post.id}`}>{post.title}</Link>
                                {post.body}

                                {post.postimageurl&&
                                    post.postimageurl.map((imageurl,j)=>{
                                        return (
                                        <div key={j}>
                                        <img src={imageurl} className="post-image"></img>
                                        </div>
                                        )
                                    })
                                }
                                {post.course}
                                {post.nickname}
                                {post.favcount}
                                {post.librarycount}
                                {/* {post.timestamp.toString()} */}
                            </div>

                      )
                            })


                }
            {this.props.user&&
                this.props.postpage?(
                    <div>
                    <div><Post user = {this.props.user} postpage={this.props.postpage} changepost={this.changepost}/></div>
                    <button onClick={this.changepost}>投稿をやめる</button>
                    </div>
                )
                :
                (
                    <a class="btn-floating btn-large waves-effect waves-light red"onClick={this.post}><i class="material-icons">add</i></a>

                )

            }


            </div>
        );
    }

}

export default Index
